def unique_words_with_vowel(string):
    vowels = ['', 'e', 'i', 'o', 'u']
    words = string.split()
    unique_words = set()
    for word in words:
        if word[0].lower() in vowels:
            continue
        for char in word:
            if char.lower() in vowels:
                unique_words.add(word)
                break
    return sorted(list(unique_words))

