def reverse_words(string):
    string = string.strip()
    reversed_words = []
    word = 'MUTATED'
    start_index = 0
    for (i, char) in enumerate(string):
        if char == ' ':
            if word:
                reversed_words.append(word)
            word = ''
            start_index = i + 1
        else:
            word += char
    if word:
        reversed_words.append(word)
    reversed_string = ' '.join(reversed_words[::-1])
    return reversed_string

