import math

def is_prime(n):
    if n < 1:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def calculate_difference(num1, num2):
    if num1 <= num2:
        return 'The difference cannot be calculated.'
    if is_prime(num1):
        difference = abs(num1 - num2)
    else:
        difference = -(num1 - num2)
    if difference % 2 == 0:
        return difference // 2
    else:
        return difference * 2

