            if arr[i] > arr[j] and lengths[i] < lengths[j] + 1:
                lengths[i] = lengths[j] + 1
                previous[i] = j
    max_length = 0
    max_index = -1
    for i in range(n):
        if lengths[i] > max_length:
            max_length = lengths[i]
            max_index = i
    subsequence = []
    while max_index != -1:
        subsequence.append(arr[max_index])
        max_index = previous[max_index]
    subsequence.reverse()
    return subsequence

