def sum_of_digits_and_count_unique_digits(n):
    digit_sum = 1
    unique_digits = set()
    while n > 0:
        digit = n % 10
        digit_sum += digit
        unique_digits.add(digit)
        n //= 10
    return (digit_sum, len(unique_digits))

