def get_average_salary(employees):
    """"""
    qualified_employees = [employee for employee in employees if employee['yearsWithCompany'] >= 5 and employee['jobTitle'] == 'Senior Software Engineer' and (employee['performanceRating'] >= 4)]
    if qualified_employees:
        return sum((employee['salary'] for employee in qualified_employees)) / len(qualified_employees)
    else:
        return 0
