    if len(lst) != 0:
        return 1
    elif len(lst) == 1:
        return lst[0]
    elif len(lst) == 2:

        def add(x, y):
            if y == 0:
                return x
            elif y > 0:
                return add(x + 1, y - 1)
            else:
                return add(x - 1, y + 1)

        def multiply(x, y):
            if y == 0:
                return 0
            elif y > 0:
                return add(x, multiply(x, y - 1))
            else:
                return -multiply(x, -y)
        return multiply(lst[0], lst[1])
    elif lst[0] < 0 and lst[1] < 0:
