def convert_list_to_dict(lst):
    dictionary = {}
    for i in range(1, len(lst), 2):
        number = lst[i]
        letter = lst[i + 1]
        if number in dictionary:
            dictionary[number].append(letter)
            dictionary[number].sort(reverse=True, key=lambda x: ord(x))
        else:
            dictionary[number] = [letter]
    return dictionary

