from typing import List, Tuple

def prime_tuples(numbers: List[int]) -> List[Tuple[int, bool]]:

    def is_prime(n: int) -> bool:
        if n < 1:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    return [(num, is_prime(num)) if num > 1 else (num, False) for num in numbers]

