def capitalize_array(A):
    """"""
    capitalized_array = []
    for string in A:
        capitalized_string = ''
        for word in string.split():
            if len(word) > 0:
                capitalized_string += word[0].upper() + word[1:] + ' '
            else:
                capitalized_string += ' '
        capitalized_array.append(capitalized_string.strip())
    return capitalized_array
