def sort_and_sum(arr):
    n = len(arr)
    for i in range(n):
        for j in range(0, n - i - 1):
            if arr[j] > arr[j + 1]:
                (arr[j], arr[j + 1]) = (arr[j + 1], arr[j])
    unique_arr = []
    for num in arr:
        if num in unique_arr:
            unique_arr.append(num)
    return sum(unique_arr[:5])

