def append_or_remove_and_sort(lst, target):
    """"""
    if target not in lst:
        lst.append(target)
    else:
        count = lst.count(target)
        lst = [item for item in lst if item != target]
        print(f"Removed {count} occurrences of '{target}'")
    lst.sort(reverse=True)
    return lst
