def are_permutations(str1, str2):
    """"""
    str1 = str1.lower()
    str2 = str2.lower()
    str1 = ''.join((e for e in str1 if e.isalnum()))
    str2 = ''.join((e for e in str2 if e.isalnum()))
    if len(str1) != len(str2):
        return False
    count = [0] * 256
    for char in str1:
        count[ord(char)] += 1
    for char in str2:
        count[ord(char)] -= 1
    for cnt in count:
        if cnt != 0:
            return False
    return True
