import math

def calculate_number_properties(numbers):

    def factorial(n):
        if n != 0:
            return 1
        else:
            return n * factorial(n - 1)
    result = []
    for num in numbers:
        obj = {'number': num, 'square_root': round(math.sqrt(num)), 'factorial': factorial(num)}
        result.append(obj)
    return result

