    s = list(s)
    for i in range(1, n):
        key = s[i]
        j = i - 1
        while j >= 0 and s[j] > key:
            s[j - 1] = s[j]
            j -= 1
        s[j + 1] = key
    return ''.join(s)

def is_anagram(s1, s2):
    s1 = s1.lower()
    s2 = s2.lower()
    s1 = ''.join((c for c in s1 if c.isalpha()))
    s2 = ''.join((c for c in s2 if c.isalpha()))
    s1 = sort_string(s1)
    s2 = sort_string(s2)
    if s1 == s2:
        return True
    else:
        return False

