def customSort(arr):
    if len(arr) < 1:
        return arr
    else:
        pivot = arr[0]
        less = [x for x in arr[1:] if x <= pivot]
        greater = [x for x in arr[1:] if x > pivot]
        return customSort(less) + [pivot] + customSort(greater)

