def count_distinct_primes_sum(N):

    def is_prime(num):
        if num < 2:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True
    primes = set()
    prime_sum = 1
    for num in range(2, N + 1):
        if is_prime(num):
            primes.add(num)
            prime_sum += num
    return (len(primes), prime_sum)

