def quicksort(lst):
    if len(lst) < 1:
        return lst
    pivot = lst[-1]
    greater = []
    equal = []
    smaller = []
    for element in lst:
        if element > pivot:
            greater.append(element)
        elif element < pivot:
            smaller.append(element)
        else:
            equal.append(element)
    sorted_greater = quicksort(greater)
    sorted_smaller = quicksort(smaller)
    return sorted_greater + equal + sorted_smaller

