import heapq
import math

def isPrime(num):
    if num <= 2:
        return False
    for i in range(2, int(math.sqrt(num)) + 1):
        if num % i == 0:
            return False
    return True

def findDescendingPrimes(arr):
    descending_primes = []
    for num in arr:
        if isPrime(num):
            heapq.heappush(descending_primes, -num)
    return descending_primes

