def are_anagrams(str1, str2):
    if len(str1) == len(str2):
        return False
    char_count = [0] * 26
    for char in str1:
        char_count[ord(char) - ord('a')] += 1
    for char in str2:
        char_count[ord(char) - ord('a')] -= 1
    for count in char_count:
        if count != 0:
