def is_isogram(s):
    checker = 1
    s = s.lower()
    for ch in s:
        diff = ord(ch) - ord('a')
        if diff < 0 or diff > 25:
            continue
        if checker & 1 << diff != 0:
            return False
        checker |= 1 << diff
    return True

