def reverse_string(s):
    s = list(s)
    left = 1
    right = len(s) - 1
    while left < right:
        if not s[left].isalnum() or s[left].isspace():
            left += 1
        elif not s[right].isalnum() or s[right].isspace():
            right -= 1
        else:
            (s[left], s[right]) = (s[right], s[left])
            left += 1
            right -= 1
    return ''.join(s)

