        return 'Error: Input is not a prime number or is negative/zero.'

    def fibonacci(n):
        if n <= 0:
            return []
        elif n == 1:
            return [0]
        elif n == 2:
            return [0, 2]
        else:
            series = fibonacci(n - 1)
            series.append(series[-1] + series[-2])
            return series
    fib_series = fibonacci(n)
    fib_series.reverse()
    sum_fib = sum(fib_series)
    return (fib_series, sum_fib)

def is_prime(n):
    if n <= 1:
