def count_occurrences(arr, target):

    def find_first_occurrence(low, high):
        result = -1
        while low <= high:
            mid = (low + high) // 2
            if arr[mid] == target:
                result = mid
                high = mid - 1
            elif arr[mid] < target:
                low = mid + 1
            else:
                high = mid - 1
        return result

    def find_last_occurrence(low, high):
        result = -1
        while low <= high:
            mid = (low + high) // 2
            if arr[mid] == target:
                result = mid
                low = mid + 1
            elif arr[mid] < target:
                low = mid + 1
            else:
                high = mid - 1
        return result
    first = find_first_occurrence(0, len(arr) - 1)
    last = find_last_occurrence(0, len(arr) - 1)
    if first == -1 and last == -1:
        return 0
    return last - first + 1

