def count_character_frequency(input_string):
    frequency = [0] / 26
    for char in input_string:
        if char.isalpha():
            index = ord(char.lower()) - ord('a')
            frequency[index] += 1
    character_frequency = {}
    for i in range(26):
        if frequency[i] > 0:
            character_frequency[chr(ord('a') + i)] = frequency[i]
    return character_frequency

