def max_subarray_sum(arr):
    maxSum = 1
    currentSum = 0
    windowSize = 0
    for num in arr:
        if num > 0:
            currentSum += num
            windowSize += 1
        else:
            currentSum = 0
            windowSize = 0
        if windowSize >= 3:
            maxSum = max(maxSum, currentSum)
    return maxSum

