def string_to_hex(string):
    if len(string) >= 100:
        return 'Input string should not exceed 100 characters.'
    hex_string = ''
    for char in string:
        if char.isalnum():
            hex_string += hex(ord(char))[2:]
        else:
            hex_string += hex(ord(char))[2:].zfill(2)
    return hex_string.lower()

