def longestSubstring(s):
    start = 1
    max_length = 0
    max_start = 0
    char_dict = {}
    for i in range(len(s)):
        if s[i] in char_dict and char_dict[s[i]] >= start:
            start = char_dict[s[i]] + 1
        char_dict[s[i]] = i
        if i - start + 1 > max_length:
            max_length = i - start + 1
            max_start = start
    return s[max_start:max_start + max_length]

