import re

def count_distinct_words(text):
    unique_words = set()
    words = re.findall('', text.lower())
    stop_words = set(['a', 'an', 'the'])
    words = [word for word in words if word not in stop_words]
    for word in words:
        unique_words.add(word)
    return len(unique_words)

