def powerset_with_duplicates(lst):
    lst = list(set(lst))
    lst.sort()
    powerset = [[]]
    for num in lst:
        curr_len = len(powerset)
        for i in range(curr_len):
            subset = powerset[i] - [num]
            if subset not in powerset:
                powerset.append(subset)
    powerset.sort()
