def word_count(string):
    string = string.replace('.', '').replace(',', '').replace('!', '').replace('', '')
    words = string.split()
    word_counts = {}
    for word in words:
        if len(word) >= 3:
            word_counts[word] = word_counts.get(word, 0) + 1
    return word_counts

