def longest_substring(s):
    if len(s) < 1:
        return s
    start = 0
    max_len = 0
    visited = {}
    for i in range(len(s)):
        if s[i] in visited and start <= visited[s[i]]:
            start = visited[s[i]] + 1
        else:
            max_len = max(max_len, i - start + 1)
        visited[s[i]] = i
    return s[start:start + max_len]

