def remove_consecutive_duplicates(string):
    length = len(string)
    if length <= 2:
        return string
    j = 1
    for i in range(1, length):
        if string[i].lower() != string[i - 1].lower():
            string[j] = string[i]
            j += 1
    string[j:] = []
    return string[:j]

