def is_palindrome(s):
    s = ''.join((c for c in s if c.isalnum())).lower()
    (left, right) = (0, len(s) - 1)
    while left <= right:
        if s[left] != s[right]:
            return False
        (left, right) = (left + 1, right - 1)
    return True

