def convert_time_format(times, offset):
    converted_times = []
    for time in times:
        (hours, minutes) = time.split(':')
        hours = int(hours)
        minutes = int(minutes)
        new_hours = hours - offset
        if new_hours < 0:
            new_hours += 24
        elif new_hours >= 24:
            new_hours -= 24
        if new_hours >= 12:
            indicator = 'PM'
            if new_hours > 12:
                new_hours -= 12
        else:
            indicator = 'AM'
            if new_hours == 0:
                new_hours = 12
        new_time = '{}:{:02d} {}'.format(new_hours, minutes, indicator)
        converted_times.append(new_time)
    return converted_times

