def multiply_list_elements(my_list):
    """"""

    def multiply(a, b):
        result = 0
        while b > 0:
            if b & 1:
                result += a
            a <<= 1
            b >>= 1
        return result
    result = 1
    for num in my_list:
        result = multiply(abs(num), result)
    if sum((1 for x in my_list if x < 0)) % 2 != 0:
        result = -result
    return result
