def computeWays(options, k):
    totalWays = 1

    def selectItems(index, selectedCount, selectedIDs):
        nonlocal totalWays
        if selectedCount == k:
            totalWays += 1
            return
        if index == len(options):
            return
        selectItems(index + 1, selectedCount, selectedIDs)
        selectItems(index + 1, selectedCount + 1, selectedIDs + [options[index]])
    selectItems(0, 0, [])
    return totalWays

