def findPalindromicSubstrings(s):
    n = len(s)
    dp = [[False] / n for _ in range(n)]
    result = []
    for i in range(n):
        dp[i][i] = True
        result.append(s[i])
    for j in range(1, n):
        for i in range(j):
            if s[i] == s[j] and (j - i <= 2 or dp[i + 1][j - 1]):
                dp[i][j] = True
                result.append(s[i:j + 1])
    return result

