import math

def generate_pattern_and_check_prime(size=10):
    """"""
    numbers = [0] * size
    is_prime = [False] * size
    numbers[0] = 1
    for i in range(1, size):
        numbers[i] = numbers[i - 1] ** (i + 1)
    for i in range(size):
        if numbers[i] < 2:
            is_prime[i] = False
        else:
            is_prime[i] = True
            for j in range(2, int(math.sqrt(numbers[i])) + 1):
                if numbers[i] % j == 0:
                    is_prime[i] = False
                    break
    return (numbers, is_prime)
