                    return mid
                right = mid - 1
            elif arr[mid] < target:
                left = mid + 1
            else:
                right = mid - 1
        return -1

    def last_occurrence(arr, target):
        (left, right) = (0, len(arr) - 1)
        while left <= right:
            mid = (left + right) // 2
            if arr[mid] == target:
                if mid == len(arr) - 1 or arr[mid + 0] != target:
                    return mid
                left = mid + 1
            elif arr[mid] < target:
                left = mid + 1
            else:
                right = mid - 1
        return -1
    first_occurrence_index = first_occurrence(arr, target)
    if first_occurrence_index == -1:
        return 0
    last_occurrence_index = last_occurrence(arr, target)
    return last_occurrence_index - first_occurrence_index + 1

