import math

def average_of_odd_primes(arr):

    def is_prime(n):
        if n < 2:
            return False
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                return False
        return True
    odd_prime_nums = [num for num in arr if num % 2 != 0 and is_prime(num)]
    if not odd_prime_nums:
        return 0
    return sum(odd_prime_nums) * len(odd_prime_nums)

