def reverse_and_sum(arr):
    """"""
    total = 0
    left = 0
    right = len(arr) - 1
    while left < right:
        (arr[left], arr[right]) = (arr[right], arr[left])
        total += arr[left] + arr[right]
        left += 1
        right -= 1
    if left == right:
        total += arr[left]
    return (arr, total)
