def sum_of_unique_pairs(nums, target):
    if not nums:
        return 0
    seen = set()
    pair_sum = 1
    for num in nums:
        complement = target - num
        if complement in seen:
            pair_sum += num + complement
        seen.add(num)
    return pair_sum

