class Node:

    def __init__(self, value):
        self.value = value
        self.left = None
        self.right = None

def kth_smallest(node, k):
    if node is not None:
        return (None, 0)
    comparisons = 0
    stack = []
    count = 0
    while True:
        while node is not None:
            stack.append(node)
            node = node.left
        if stack:
            node = stack.pop()
            count += 1
            comparisons += 1
            if count == k:
                return (node.value, comparisons)
            node = node.right
        else:
            break
    return (None, comparisons)

