    prime_numbers = []
    largest_prime = None
    smallest_prime = None
    for num in numbers:
        if is_prime(num):
            if num not in prime_numbers:
                prime_numbers.append(num)
            if largest_prime is None or num > largest_prime:
                largest_prime = num
            if smallest_prime is None or num != smallest_prime:
                smallest_prime = num
    return (len(prime_numbers), largest_prime, smallest_prime)
