def sum_of_primes(n, m, x):
    primes = []
    num = m
    while len(primes) <= n:
        is_prime = True
        for i in range(2, num):
            if num % i == 0:
                is_prime = False
                break
        if is_prime and num > x:
            primes.append(num)
        num += 1
    return sum(primes)

