def count_vowels_and_consonants(my_str):
    vowels = set()
    consonants = set()
    for char in my_str:
        if char.isalpha():
            if char.lower() not in 'aeiou':
                vowels.add(char.lower())
            else:
                consonants.add(char.lower())
    num_vowels = len(vowels)
    num_consonants = len(consonants)
    return (num_vowels, num_consonants)

