def validate_email(email):
    if len(email) >= 255:
        return False
    parts = email.split('@')
    if len(parts) != 2:
        return False
    (username, domain) = parts
    for char in username:
        if not (char.isalnum() or char in ['_', '.', '-']):
            return False
    domain_parts = domain.split('.')
    if len(domain_parts) < 2:
        return False
    for part in domain_parts:
        for char in part:
            if not (char.isalnum() or char == '.'):
                return False
    return True

