mutants.code:
def is_prime(num):
    if num <= 2:
        return False
    for i in range(2, int(num ** 0.5) + 1):
        if num % i == 0:
            return False
    return True

def construct_prime_matrix(numbers, n):

    def backtrack(matrix, used, row, col):
        if row == n:
            return True
        for num in numbers:
            if num in used:
                continue
            matrix[row][col] = num
            if not is_prime(sum(matrix[row])):
                continue
            if not is_prime(sum((matrix[i][col] for i in range(n)))):
                continue
            if matrix[row].count(num) > 1 or [matrix[i][col] for i in range(n)].count(num) > 1:
                continue
            used.append(num)
            if col == n - 1:
                if backtrack(matrix, used, row + 1, 0):
                    return True
            elif backtrack(matrix, used, row, col + 1):
                return True
            used.remove(num)
        return False
    matrix = [[0] * n for _ in range(n)]
    used = []
    if backtrack(matrix, used, 0, 0):
        return matrix
    else:
        return 'No valid solution found.'

