import re
from collections import defaultdict

def count_words(text):
    text = re.sub('', '', text.lower())
    word_counts = defaultdict(int)
    for word in text.split():
        if len(word) >= 3:
            word_counts[word] += 1
    sorted_words = sorted(word_counts.items(), key=lambda x: (-x[1], x[0]))
    return sorted_words

